    public static List<int[,]> LAYOUTS = new List<int[,]>();

    public ObstacleLayouts()
    {
        LAYOUTS.Add(PLUS_LAYOUT);
        LAYOUTS.Add(ROOM_LAYOUT);
        LAYOUTS.Add(LOSS_LAYOUT);
        LAYOUTS.Add(FOUR_SQUARE_LAYOUT);
        LAYOUTS.Add(HORSESHOE_LAYOUT);
    }

    public static int[,] GetRandomLayout()
    {
        return LAYOUTS[Random.Range(0, LAYOUTS.Count - 1)];
    }

    public static int GetRandomLayoutIndex()
    {
        return Random.Range(0, LAYOUTS.Count - 1);
    }

    public static int[,] GetLayout(int _layoutIndex)
    {
        int[,] invertedLayout = new int[LAYOUTS[_layoutIndex].GetUpperBound(0), LAYOUTS[_layoutIndex].GetUpperBound(1)];

        int invertedIndex = 0;
        for (int i = LAYOUTS[_layoutIndex].GetUpperBound(0) - 1; i >= 0; i--)
        {
            for (int j = 0; j < LAYOUTS[_layoutIndex].GetUpperBound(1); j++)
            {
                invertedLayout[invertedIndex, j] = LAYOUTS[_layoutIndex][i, j];
            }

            invertedIndex++;
        }

        return invertedLayout;
    }
